    '@context': 'https://schema.org',
    '@type': 'BlogPosting',
    '@id': `${articleUrl}#article`,
    'headline': post.title,
    'description': post.excerpt,
    'abstract': post.tldr?.summary.join(' ') || post.excerpt,
    'image': {
